mapper.insert:



SELECT NVL(MAX(empid), 0) + 1
FROM emp

INSERT INTO emp(empid, empname, mgr, sal

	, deptid

, hire_date)
VALUES(#{empid},#{empname},#{mgr},#{sal},#{deptid}


	    , #{hire_date}


		, sysdate


)

mapper.update:
UPDATE emp
SET empname = #{emp.empname}
	,sal = #{emp.sal}
	,mgr = #{emp.mgr}
	,deptid = #{emp.deptid}
WHERE empid = #{id}
